import React from "react";
import "./App.css";
import Box from "@mui/material/Box";
import ToggleButton from "@mui/material/ToggleButton";
import ToggleButtonGroup from "@mui/material/ToggleButtonGroup";

//main color rgb(35 36 44);
//box color rgb(57 58 66)

function App() {
  const [alignment, setAlignment] = React.useState("web");

  const [alignment2, setAlignment2] = React.useState("web2");

  const handleChange = (event, newAlignment) => {
    setAlignment(newAlignment);
  };

  const handleChangeSub = (event, newAlignment2) => {
    setAlignment2(newAlignment2);
  };

  return (
    <div className="App">
      <div className="box-2">
        <h1 className="title">Trnslte</h1>
      </div>

      <div className="box-2">
        <p className="title">This is a description</p>
      </div>

      <div className="box-2">
        <Box
          sx={{
            width: 800,
            height: 400,
            backgroundColor: "rgb(57 58 66)",
            alignItems: "center",
            fontSize: "30",
            borderRadius: 2,
            marginTop: "10vw",
            textAlign: "center",
          }}
        >
          <h1 className="title">Language of your video</h1>

          <ToggleButtonGroup
            sx={{
              position: "center",
              marginTop: 2,
            }}
            color="primary"
            value={alignment}
            exclusive
            onChange={handleChange}
          >
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              thumbStyle={{ backgroundColor: "red" }}
              trackStyle={{ backgroundColor: "green" }}
              value="web"
            >
              Web
            </ToggleButton>
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              value="android"
            >
              Android
            </ToggleButton>
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              value="ios"
            >
              iOS
            </ToggleButton>
          </ToggleButtonGroup>

          <h1 className="title">Language of the subtitles </h1>

          <ToggleButtonGroup
            sx={{
              position: "center",
              marginTop: 2,
            }}
            color="primary"
            value={alignment2}
            exclusive
            onChange={handleChangeSub}
          >
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              thumbStyle={{ backgroundColor: "red" }}
              trackStyle={{ backgroundColor: "green" }}
              value="web2"
            >
              Web
            </ToggleButton>
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              value="android"
            >
              Android
            </ToggleButton>
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              value="ios"
            >
              iOS
            </ToggleButton>
          </ToggleButtonGroup>
        </Box>



      </div>
    </div>
  );
}

export default App;
