import React, { useEffect, useState } from "react";
import "./App.css";
import Box from "@mui/material/Box";
import ToggleButton from "@mui/material/ToggleButton";
import ToggleButtonGroup from "@mui/material/ToggleButtonGroup";
import Button from "@mui/material/Button";
import Alert from "@material-ui/lab/Alert";
import AlertTitle from "@material-ui/lab/AlertTitle";
import Grid from "@mui/material/Grid";
import axios from "axios";

//main color rgb(35 36 44);
//box color rgb(57 58 66)

function App() {
  const [alignment, setAlignment] = React.useState("web");
  const [alignment2, setAlignment2] = React.useState("web2");
  const [file, getFile] = useState(null);
  const [tree, setTree] = React.useState(0);
  const [FilePath, setFilePath] = useState("");
  const [filename, setFilename] = useState("Choose File");
  const [person, setPerson] = React.useState("hey");

  // For the error box
  const [fileFormatError, setErrorFileFormat] = React.useState(false);

  const componentDidMount = async () => {
    const url = "https://randomuser.me/api/";
    const response = await fetch(url);
    const data = await response.json();
    console.log(data.results[0].name.first);
    setPerson(data.results[0].name.first);
  };

  const click = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/home`);
      console.log(res);
    } catch (err) {
      console.log(err);
    }
  };

  let download = (e) => {
    const fileName = e.target.parentElement.id;
    axios({
      method: "get",
      url: "http://localhost:3001/download" + fileName,
      responseType: "blob",
      headers: {},
    })
      .then((res) => {
        const url = window.URL.createObjectURL(new Blob([res.data]));
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", fileName);
        document.body.appendChild(link);
        link.click();
      })
      .catch((error) => {
        alert(error);
      });
  };

  const onDrop = async () => {
    console.log(typeof file);
    var formData = new FormData();
    formData.append("file", file);
    try {
      const res = await axios.post("http://localhost:3001/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      const { fileName, filePath } = res.data;
      //console.log(res);
    } catch (err) {
      if (err) {
        console.log("this is the err", err);

        setErrorFileFormat(true);
      } else {
        console.log(err.response.data.msg);
        download();
      }
    }
  };

  const handleChange = (event, newAlignment) => {
    setAlignment(newAlignment);
  };

  const handleChangeSub = (event, newAlignment2) => {
    setAlignment2(newAlignment2);
  };

  return (
    <div className="App">
      <div className="box-2">
        <div className="box-3">
          <div className="box-title">
            <h1 className="title">Sub</h1>
          </div>
          <div className="box-description">
            <p className="title">
              Welcome to Sub! Sub is an online service where you send us your video and we add subtitles to it. 
            </p>
          </div>

          <div className="downloadBox">

              <Button
                style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              >
                <input
                  /*
                  multiple={false}

                  value={file}
                  style={{ color: "transparent" }}
                  */
                  type="file"
                  onChange={(e) => getFile(e.target.files[0])}
                />
              </Button>

          </div>

          <Button color="primary" onClick={onDrop}>
            Add subtitle
          </Button>

          <Button color="primary" onClick={download}>
            Download
          </Button>



      </div>

      {/* This is the alertBox  */}
      {fileFormatError ? (
        <div className="alertDiv">
          <Box display="flex" alignItems="center" justifyContent="center">
            <Alert severity="error">Sorry, we only accept mp4 files</Alert>
          </Box>
        </div>
      ) : (
        <div> </div>
      )}
      </div>
    </div>
  );
}

export default App;
