import React from 'react';
import './App.css';
import Box from '@mui/material/Box';
import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';



//main color rgb(35 36 44);
//box color rgb(57 58 66)

function App() {

  const [alignment, setAlignment] = React.useState('web');

  const handleChange = (event, newAlignment) => {
    setAlignment(newAlignment);
  };


  return (
    <div className="App">

      <div className='box-2'>
        <h1 className='title' >Trnslte</h1>

      </div>

      <div className='box-2'>
      <p className='title'>This is a description</p>
      </div>

    <div className='box-2'>

<Box
      sx={{
        width: 300,
        height: 300,
        backgroundColor: 'rgb(57 58 66)',
        alignItems:'center',
        fontSize:'30',
        borderRadius: 2,
        marginTop:'10vw',
        textAlign:'center'

      }}
    >

  <p className='title'>Language of your video</p>

<ToggleButtonGroup

      sx={{
        position:'center',
        marginTop:2,
      }
      }
      color="primary"
      value={alignment}
      exclusive
      onChange={handleChange}
    >
      <ToggleButton value="web">Web</ToggleButton>
      <ToggleButton value="android">Android</ToggleButton>
      <ToggleButton value="ios">iOS</ToggleButton>
    </ToggleButtonGroup>

    <h1></h1>


      </Box>


    </div>

    </div>
  );
}

export default App;
