import React, { useEffect, useState } from "react";
import "./App.css";
import Box from "@mui/material/Box";
import ToggleButton from "@mui/material/ToggleButton";
import ToggleButtonGroup from "@mui/material/ToggleButtonGroup";
import Button from "@mui/material/Button";
import Alert from '@material-ui/lab/Alert';
import AlertTitle from '@material-ui/lab/AlertTitle';
import axios from 'axios'

//main color rgb(35 36 44);
//box color rgb(57 58 66)

function App() {
  const [alignment, setAlignment] = React.useState("web");
  const [alignment2, setAlignment2] = React.useState("web2");
  const [file, getFile] = useState(null);
  const[tree, setTree] = React.useState(0);
  const [FilePath, setFilePath] = useState("")
  const [filename, setFilename] = useState('Choose File');
  const [person, setPerson] = React.useState("hey"); 

  const  componentDidMount = async () => {
    const url ="https://randomuser.me/api/";
    const response = await fetch(url);
    const data = await response.json();
    console.log(data.results[0].name.first)
    setPerson(data.results[0].name.first)
  }


  //useEffect does x when the page is loaded
  /*
  useEffect(() =>{
    fetch("http://localhost:3001/api").then(
      res => res.json()
    ).then(
      data => { 
        console.log(data)
      }
    )
  }, [])
  */


  const getNum =  () => {
    const url = "http://localhost:3001/num";
     fetch(url).then(
      res => res.json()
    ).then(
      data => {
        console.log("data " + data)
        setTree(data + tree)
      }
    )
  }

  const sendNum =   () =>{
    const url = "http://localhost:3001/sendData";
     fetch(url, {
      headers: {
        'Content-Type': 'application/json',
    },
      method: 'POST',
      body: JSON.stringify({ number: tree })
    })

  }

  const getTree = () =>{
    sendNum()
    getNum()
  }

  const onDrop = async  () => {
    console.log(typeof file)
    var formData = new FormData();
    formData.append("file", file)
    try {
      const res = await axios.post('http://localhost:3001/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },

      });

      const { fileName, filePath } = res.data;

      console.log(res)

    } catch (err) {
      if (err.response.status === 500) {
        console.log("error server")
      } else {
        console.log(err.response.data.msg)

      }

    }
    /*
    formData.append("file", file)
    console.log(formData)
    */
   /*
    axios({
      method: "POST",
      url: "http://localhost:3001/sendVideo",
      data: formData,
       })
      .then(function (response) {
        //handle success
        console.log(response);
      })
      .catch(function (response) {
        //handle error
        console.log(response);
      });
      */

    /*
    const url = "http://localhost:3001/sendVideo";
    fetch(url, {
    method: 'POST',
    body: formData,
    })
    */

}

  const handleChange = (event, newAlignment) => {
    setAlignment(newAlignment);
  };

  const handleChangeSub = (event, newAlignment2) => {
    setAlignment2(newAlignment2);
  };

  /*send data to the backend */




  return (
    <div className="App">
      <div className="box-title">
        <h1 className="title">Trnslte</h1>
        <Alert severity="error">This is an error alert — check it out!</Alert>
      </div>

      <div className="box-description">
        <p className="title">
          Welcome to my first saas! With this service you can put your video in
          the little box and it will ad subtitles in your selected language.{" "}
        </p>
      </div>

      <div className="box-2">
        <Box
          sx={{
            width: "75vw",
            height: "75wh",
            backgroundColor: "rgb(57 58 66)",
            alignItems: "center",
            fontSize: "30",
            borderRadius: 2,
            marginTop: "5vw",
            textAlign: "center",
          }}
        >
          <div className="inbox-title">
            <h1 className="title">Language of your video</h1>
          </div>
          <ToggleButtonGroup
            sx={{
              position: "center",
              marginTop: 2,
            }}
            value={alignment}
            exclusive
            onChange={handleChange}
          >
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              value="web"
            >
              Web
            </ToggleButton>
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              value="android"
            >
              Android
            </ToggleButton>
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              value="ios"
            >
              iOS
            </ToggleButton>
          </ToggleButtonGroup>

          <h1 className="title">Language of the subtitles </h1>

          <ToggleButtonGroup
            sx={{
              position: "center",
              marginTop: 2,
            }}
            value={alignment2}
            exclusive
            onChange={handleChangeSub}
          >
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              value="web2"
            >
              Web
            </ToggleButton>
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              value="android"
            >
              Android
            </ToggleButton>
            <ToggleButton
              style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              value="ios"
            >
              iOS
            </ToggleButton>
          </ToggleButtonGroup>

          <div className="downloadBox">
            <Box component="span" sx={{ p: 2, border: "1px dashed grey" }}>
              <Button
                style={{ fontWeight: "bold", fontSize: "18px", color: "white" }}
              >
                Your video
                <input

                  /*
                  multiple={false}

                  value={file}
                  style={{ color: "transparent" }}
                  */
                  type="file"
                  onChange={(e) => getFile(e.target.files[0])}

                />
              </Button>


            </Box>

          </div>

          <div>


          </div>
          <Button
                color="primary"
                onClick={onDrop}
              >
                Click here
              </Button>
        </Box>
      </div>
    </div>
  );
}

export default App;
